require_once 'scripts/includes/global.php';
require_once 'scripts/includes/newfunctions.php';
mb_internal_encoding("UTF-8");
mb_regex_encoding("UTF-8");

echo "Card Generator v$version - MTGJSON to cards.csv\n\n";

/*$files = getInputFiles(
	array_slice($argv, 1),
	'Drag and drop an MTGJSON file here and press enter...'
);*/

if (empty($argv[1])) die("The json file name is missing\n");
$filename = $argv[1];

$json = file_get_contents($filename);
$array = json_decode($json, true);
echo "Creating temporary file: data/cards.csv.temp\n";
$f = fopen('data/cards.csv.tmp', 'w');

$firstLineKeys = false;
$count = count($array['cards']);
foreach ($array['cards'] as $line)
{
    if (empty($firstLineKeys))
    {
        $firstLineKeys = array('name','set','color','Type','p/t','flavor','rarity','manaCost','text','version','artist','number');
        fputcsv($f, $firstLineKeys);
        //$firstLineKeys = array_flip($firstLineKeys);
    }
	$pt = @$line['power'] . '\\' . @$line['toughness'];
	if ($pt == '\\') $pt = '';
	$number = $line['number'] . '\\' . $count;
	$rarity = substr($line['rarity'], 0, 1);
	if ($rarity == 'B') $rarity = 'C';
	if ($line['originalType'] == 'Land' || substr($line['originalType'],6,4) == 'Land') $line['colorIdentity'] = 'Lnd';
	if (substr($line['originalType'],0,8) == 'Artifact') $line['colorIdentity'] = 'Art';
	$line['name'] = transliterateString($line['name']);
	$line['text'] = preg_replace('~\"(\w)~','“\1',$line['text']);
	$line['text'] = preg_replace('~\"(\n)~','”\1',$line['text']);
	$line['text'] = str_replace("'", '’', $line['text']);
	$line['flavor'] = preg_replace('~\"\"(\w)~','“\1',$line['flavor']);
	$line['flavor'] = preg_replace('~\"\"(\n)~','”\1',$line['flavor']);
	$line['flavor'] = str_replace("'", '’', $line['flavor']);
	//if (!empty($line['flavor'])) $line['flavor'] = preg_replace('/\"\"([\p{L}\p{P}]*?)\"\"/', '“\\1”', $line['flavor']);
    $line_array = array($line['name'],$array['code'],$line['colorIdentity'],/*$line['originalType'] != '' ? $line['originalType'] : */$line['type'],@$pt,@$line['flavor'],$rarity,@$line['manaCost'],/*$line['originalText'] != '' ? $line['originalText'] : */$line['text'],'',$line['artist'],$number);
	$colorID = null;
	//$colors = null;
    if ($line['colorIdentity'][0]) {
        $colorID = Card::getCostColors($line['manaCost']);
    }
	if (!empty($colorID) && strlen($colorID) >= 2 && $colorID != 'Lnd' && $colorID != 'Art') $colorID = 'Gld';
	if (!empty($colorID)) $line_array[2] = $colorID;
	/*foreach ($line['colors'] as $key => $value)
    {
        $colors .= $value;
    }
	$line_array['colors'] = $colors;*/
    //array_push($line_array,$line['name']);
    fputcsv($f, $line_array);

}
if (file_exists('data/cards.csv')) {
	echo "Backing up file \"data/cards.csv\" to \"data/cards.csv.bak\"...\n";
	@unlink('data/cards.csv.bak');
	@rename('data/cards.csv', 'data/cards.csv.bak');
}

echo "Moving temporary file to \"data/cards.csv\"...\n";
rename('data/cards.csv.temp', 'data/cards.csv');

echo "Import complete.\n";

